hm<span class="token punctuation">.</span><span class="token function">put</span><span class="token punctuation">(</span><span class="token string">"吴九"</span><span class="token punctuation">,</span> <span class="token string">"宁波"</span><span class="token punctuation">)</span><span class="token punctuation">;</span>
