gui<span class="token punctuation">.</span><span class="token function">add</span><span class="token punctuation">(</span>eventObj<span class="token punctuation">,</span><span class="token string">"Fullscreen"</span><span class="token punctuation">)</span><span class="token punctuation">.</span><span class="token function">name</span><span class="token punctuation">(</span><span class="token string">"全屏"</span><span class="token punctuation">)</span>
