<span class="token keyword">const</span> x <span class="token operator">=</span> <span class="token operator">-</span><span class="token number">2.5</span><span class="token punctuation">;</span>
<span class="token keyword">const</span> y <span class="token operator">=</span> <span class="token operator">-</span><span class="token number">5</span><span class="token punctuation">;</span>
shape<span class="token punctuation">.</span><span class="token function">moveTo</span><span class="token punctuation">(</span>x <span class="token operator">+</span> <span class="token number">2.5</span><span class="token punctuation">,</span> y <span class="token operator">+</span> <span class="token number">2.5</span><span class="token punctuation">)</span><span class="token punctuation">;</span>
